<?php

namespace DBundle\Repository;

/**
 * ContribuablesDfuContenuRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ContribuablesDfuContenuRepository extends \Doctrine\ORM\EntityRepository
{
    public function uptdateObs($id)
    {
        $conn= $this->getEntityManager()
                    ->getConnection();
                    //$sql="SELECT * FROM `Tache` ";
        // $sql = "UPDATE `ContribuablesDfuContenu` SET `status` = 'Traité' WHERE ContribuableId = ".$id." ";
        $sql = "UPDATE `ContribuablesDfuContenu` SET `status` = 'Traité' WHERE `id` = ".$id." ";
        $stmt = $conn->prepare($sql);
        return $stmt->execute();
    }

    public function uptdateObsferme($id)
    {
        $conn= $this->getEntityManager()
                    ->getConnection();
                    //$sql="SELECT * FROM `Tache` ";
        $sql = "UPDATE `ContribuablesDfuContenu` SET `status` = 'Fermé' WHERE ContribuableId = ".$id." ";
        $stmt = $conn->prepare($sql);
        return $stmt->execute();
    }
}
